import React, { useEffect, useState } from 'react';
import { useSubscription, gql } from '@apollo/client';
import client from './ApolloClient'; // your ApolloClient.js

// GraphQL subscription
const BOOK_ADDED_SUBSCRIPTION = gql`
  subscription {
    bookAdded {
      id
      title
      author
      totalCopies
      availableCopies
    }
  }
`;

function BookList() {
    const [books, setBooks] = useState([]);

    // Apollo subscription hook
    const { data, loading, error } = useSubscription(BOOK_ADDED_SUBSCRIPTION, {
        client, // use the Apollo client with WS link
        onSubscriptionData: ({ subscriptionData }) => {
            if (!subscriptionData.data) return;

            const newBook = subscriptionData.data.bookAdded;
            setBooks(prevBooks => {
                // avoid duplicates
                if (prevBooks.some(b => b.id === newBook.id)) return prevBooks;
                return [...prevBooks, newBook];
            });
        },
    });

    // Optional: handle errors
    useEffect(() => {
        if (error) console.error('Subscription error:', error);
    }, [error]);

    if (loading && books.length === 0) return <p>Loading books...</p>;

    return (
        <div>
            <h2>Live Book List</h2>
            <ul>
                {books.map(book => (
                    <li key={book.id}>
                        <strong>{book.title}</strong> by {book.author} â€”{' '}
                        {book.availableCopies}/{book.totalCopies} copies available
                    </li>
                ))}
            </ul>
        </div>
    );
}

export default BookList;
